import pandas as pd 
import glob 

cost_factors_list = glob.glob("cost_factors/*.csv")

verifica_columnas_name = ['difference_variable', 'multiplier', 'multiplier unit', 'annual change',
       'output_variable_name', 'output_display_name', 'sum',
       'natural multiplier units', 'display_notes', 'internal_notes']
verifica_columnas_name = [i.replace(" ","_") for i in verifica_columnas_name]

## Homogenizamos los nombres de las columnas de los csvs
## Además, los acumulamos para distinguir entre cost factos y technical costs

cost_factors = []

for i in cost_factors_list:
    print(i)
    cf = pd.read_csv(i, encoding="latin")
    cf.columns = verifica_columnas_name
    cf.to_csv(i, index = False, encoding="utf-8")
    cost_factors.append(cf)

cost_factors = pd.concat(cost_factors, ignore_index = False)
cost_factors["cost_type"] = "system_cost"

## Cargamos los transformation cost
transformation_cost = pd.read_csv("transformation_cost_definition/transformation_cost_definitions.csv", encoding = "latin")
transformation_cost["cost_type"] = "transformation_cost"
transformation_cost.columns = [i.replace(".","_").replace(" ","_") for i in transformation_cost.columns]

## Actualizamos los códigos de las transformaciones para transformation_cost
attribute_transformer_code = pd.read_csv("attribute_transformer_code/attribute_transformer_code.csv")
ssp_tx_code = set(attribute_transformer_code.transformer_code.apply(lambda x : ":".join(x.split(":")[1:])))

coinciden_transformaciones = ssp_tx_code.intersection(transformation_cost.transformation_code.apply(lambda x : ":".join(x.split(":")[1:])))
no_coinciden_transformaciones = ssp_tx_code - set(coinciden_transformaciones)

old2new_transformer_code = {'AGRC:EXPAND_CONSERVATION_AGRICULTURE' : 'AGRC:INC_CONSERVATION_AGRICULTURE',
 'CCSQ:INCREASE_CAPTURE' : 'CCSQ:INC_CAPTURE',
 'GNRL:DEC_RED_MEAT_CONSUMPTION' : 'PFLO:INC_HEALTHIER_DIETS',
 'INEN:FUEL_SWITCH_HI_AND_LO_HEAT' : 'INEN:SHIFT_FUEL_HEAT',
 'INEN:FUEL_SWITCH_HI_HEAT' : 'INEN:SHIFT_FUEL_HEAT',
 'INEN:FUEL_SWITCH_LO_HEAT': 'INEN:SHIFT_FUEL_HEAT',
 'IPPU:BUNDLE_DEC_FGAS' : 'IPPU:BUNDLE_DEC_FGAS', ### NO ENCONTRAMOS CORRESPONDENCIA
 'PFLO:IND_INC_CCS' : 'PFLO:INC_IND_CCS',
 'SCOE:FUEL_SWITCH_HEAT' : 'SCOE:SHIFT_FUEL_HEAT',
 'TRNS:FUEL_SWITCH_LIGHT_DUTY' : 'TRNS:SHIFT_FUEL_LIGHT_DUTY',
 'TRNS:FUEL_SWITCH_MARITIME' : 'TRNS:SHIFT_FUEL_MARITIME',
 'TRNS:FUEL_SWITCH_MEDIUM_DUTY' : 'TRNS:SHIFT_FUEL_MEDIUM_DUTY',
 'TRNS:FUEL_SWITCH_RAIL' : 'TRNS:SHIFT_FUEL_RAIL',
 'TRNS:INC_EFFICIENCY' : 'TRNS:INC_EFFICIENCY_NON_ELECTRIC',
 'TRNS:INC_EFFICIENCY_ELEC' : 'TRNS:INC_EFFICIENCY_ELECTRIC',
 'TRNS:INC_EFFICIENCY_NON_ELEC' : 'TRNS:INC_EFFICIENCY_NON_ELECTRIC'}

## Actualizamos nomenclatura
transformation_cost.transformation_code = "TX:" + transformation_cost.transformation_code.apply(lambda x : ":".join(x.split(":")[1:])).replace(old2new_transformer_code)

## La tabla tx podría contener los campos ['output_variable_name', 'output_display_name', 'internal_notes', 'display_notes', 'cost_type'], donde la llave primaria sería output_variable_name
tx_table_fields = ['output_variable_name', 'output_display_name', 'internal_notes', 'display_notes', 'cost_type']
tx_table = pd.concat([cost_factors[tx_table_fields], transformation_cost[tx_table_fields]], ignore_index=True)


## Actualizamos los códigos de las transformaciones para strategy_interaction_definitions
strategy_interaction_definitions = pd.read_csv("strategy_interaction_definitions/strategy_interaction_definitions.csv")


coinciden_transformaciones_strategy_interaction = ssp_tx_code.intersection(strategy_interaction_definitions.transformation_code.apply(lambda x : ":".join(x.split(":")[1:])))
no_coinciden_transformaciones_strategy_interaction = ssp_tx_code - set(coinciden_transformaciones)

strategy_interaction_definitions.transformation_code = "TX:" + strategy_interaction_definitions.transformation_code.apply(lambda x : ":".join(x.split(":")[1:])).replace(old2new_transformer_code)


