##### Guardamos cost factors. Agregamos el tipo de función de cb que necesita cada tabla de cost factors
system_cost_factors = pd.read_csv("system_cost_list/system_cost_factors_list.csv").query("include==1")
system_cost_factors_list = list(system_cost_factors.system_cost_filename)
system_cost_factors_dict = {i:j for i,j in system_cost_factors[["system_cost_filename", "cb_function"]].to_records(index = False)}


cost_factors_fields = ['difference_variable', 'multiplier', 'multiplier_unit', 'annual_change', 'output_variable_name', 'output_display_name', 'sum', 'natural_multiplier_units', 'display_notes', 'internal_notes']
cost_factors_fields.remove('output_variable_name')
cost_factors_fields = ["output_variable_name"] + cost_factors_fields


aggregate_cb_cost_factors = []

for i in cost_factors_list:
    cf = pd.read_csv(i, encoding="latin")
    cf["cb_function"] = "cb_strategy_specific_function"

    tabla_cost_factor = i.split("/")[-1]

    if tabla_cost_factor in system_cost_factors_list:
        cf["cb_function"] = system_cost_factors_dict[tabla_cost_factor]

        cf = cf[cost_factors_fields + ["cb_function"]]
    else:
        cf = cf[cost_factors_fields + ["cb_function"]]
    
    cf["cb_var_group"] = tabla_cost_factor.split(".")[0]
    aggregate_cb_cost_factors.append(cf)

aggregate_cb_cost_factors = pd.concat(aggregate_cb_cost_factors, ignore_index = True)
aggregate_cb_cost_factors.loc[aggregate_cb_cost_factors.cb_function=='cb_wali_sanitation_costs', "cb_function"] = "cb_strategy_specific_function"

aggregate_cb_cost_factors.to_csv(f"csv2sqlite/cb_cost_factors.csv", index = False)


